/*
 * BLUE GENE only
 * check_for_bgl_nodes - disallow node_group_enable if there are bgl nodes
 */
/**
 * @file    avr_attr.c
 *
 * @brief
 * avr_attr.c - contains functions related to server attributes.
 *
 * Functions included are:
 * 	check_for_bgl_nodes
 */
/**
 * @brief
 * 		check_for_bgl_nodes	-	not used anywhere
 */
int
check_for_bgl_nodes(attribute *patr,  void *pobject,  int actmode)
{
        extern int have_blue_gene_nodes;

        if ((have_blue_gene_nodes != 0)      &&
                ((patr->at_flags & ATR_VFLAG_SET) != 0) &&
                (patr->at_val.at_long != 0))
                return PBSE_NGBLUEGENE;
        return 0;
}

